sys_extension_point.example:
var CanReserve = Class.create();
CanReserve.prototype = {
    initialize: function() {
    },

	canReserve:function(thing, from, until, by) {
			return true;
	},

	handles: function(thing) {
		return false;
	},

    process: function(/*array*/ data) {
        data.push("new entry");
        return data;
    },

    type: 'CanReserve'
};
